INSERT INTO `pet`.`game` (`ID`, `PIC_FILE_ID`, `DOC_FILE_ID`, `NAME_CN`, `NAME_EN`, `DESCRIPTION_CN`, `DESCRIPTION_EN`, `LAST_UPDATE_ADMIN_ID`, `LAST_UPDATE_TIME`, `PRIORITY`, `IS_ENABLE`, `VISIT_CNT`, `FAV_CNT`) VALUES ('1', '1', NULL, '迎国庆,贺中秋', 'HAPPY HOLIDAY', '<p>国庆节是由一个国家制定的用来纪念国家本身的<a href=\"https://baike.baidu.com/item/%E6%B3%95%E5%AE%9A%E5%81%87%E6%97%A5\" target=\"_blank\">法定假日</a>。</p>\n\n<p>它们通常是这个国家的独立、宪法的签署、元首诞辰或其他有重大纪念意义的周年纪念日；也有些是这个国家守护神的圣人节。</p>\n\n<p>虽然绝大部分国家都有类似的纪念日，但是由于复杂的政治关系，部分国家的这一节日不能够称为国庆日，比如美国只有<a href=\"https://baike.baidu.com/item/%E7%8B%AC%E7%AB%8B%E6%97%A5/1886205\" target=\"_blank\">独立日</a>，没有国庆日，但是两者意义相同。</p>\n\n<p>而中国古代把<a href=\"https://baike.baidu.com/item/%E7%9A%87%E5%B8%9D/886\" target=\"_blank\">皇帝</a><a href=\"https://baike.baidu.com/item/%E5%8D%B3%E4%BD%8D\" target=\"_blank\">即位</a>、诞辰称为&ldquo;<a href=\"https://baike.baidu.com/item/%E5%9B%BD%E5%BA%86\" target=\"_blank\">国庆</a>&rdquo;。</p>\n\n<p>如今，<a href=\"https://baike.baidu.com/item/%E4%B8%AD%E5%9B%BD%E5%9B%BD%E5%BA%86%E8%8A%82\" target=\"_blank\">中国国庆节</a>特指<a href=\"https://baike.baidu.com/item/%E4%B8%AD%E5%8D%8E%E4%BA%BA%E6%B0%91%E5%85%B1%E5%92%8C%E5%9B%BD\" target=\"_blank\">中华人民共和国</a>正式成立的纪念日10月1日。</p>', 'aaaaaa', '5', '2017-09-25 18:25:09', '1', '1', '1', '0');
INSERT INTO `pet`.`game` (`ID`, `PIC_FILE_ID`, `DOC_FILE_ID`, `NAME_CN`, `NAME_EN`, `DESCRIPTION_CN`, `DESCRIPTION_EN`, `LAST_UPDATE_ADMIN_ID`, `LAST_UPDATE_TIME`, `PRIORITY`, `IS_ENABLE`, `VISIT_CNT`, `FAV_CNT`) VALUES ('2', '2', NULL, '奔达杯”第六届汽车轮毂(概念)设计大赛作品征集公告', 'HAPPY NEW YEAR', '<p>一、主题：重燃激情 创意迸发</p>\n\n<p>二、大赛目的：通过时尚创新的设计，推动中国汽车轮毂设计的发展，走向世界。 &nbsp;</p>\n\n<p>三、主办单位： 铝车轮质量协会</p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 协办单位：</p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 佛山市南海奔达模具有限公司</p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 广州美术学院工业设计学院</p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 清华大学美术学院工业设计系</p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 湖南大学设计艺术学院</p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 燕山大学</p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 广东省轻工学院</p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 承办单位：</p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 江苏长三角轮毂科技有限公司</p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 媒体支持单位： CCTV 、 新华社、 人民日报、 中国国际广播电台 、 上海东方卫视 、新民晚报、 南方都市报、香港文汇报、 中国汽车报 、汽车导报、 中国汽车画报 （待定） 新媒体：腾讯、新浪、无敌改装网、第一改装网 其他专业媒体：《中国汽车零部件》《中国质量万里行》《汽车纵横》《中国汽车工业年鉴》《中国铝业》《轻合金加工技术》《铸造》《特种铸造及有色合金》《中国汽车供应商网》《铝合金轮毂网》《轮胎世界网》《齐鲁橡胶》《汽车市场》《中国汽摩配》《汽车维修与保养》&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</p>\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 参赛要求</p>\n\n<p>A．参赛作品应该是&ldquo;实用性&rdquo;与&ldquo;艺术性&rdquo;、&ldquo;概念性&rdquo;的有机融合，要求设计者深入生活、融入生活、充分发挥想象与创造力，发掘生活中的丰富多彩，用感性、前瞻、创新的设计表现主题</p>\n\n<p>B．参赛作品必须是参赛者亲自制作、并是尚未以任何形式公开发表的原始设计，且无抄袭仿冒。为保证评审的公正性，在作品正面及背面不可以出现参赛内容以外的任何文字和标记。</p>\n\n<p>C．选手须提供精细的手绘图或三维电脑制作图（含正面、侧面、装车效果图）</p>\n\n<p>D．参赛作品必须同时提交一份100字以内的设计说明。</p>\n\n<p>E．每件作品均需打印A4幅面300dpi一份，并提供版面电子文档（保存为jpg格式，分辨率为300dpi）</p>\n\n<p>F．设计作品不予退回，请作者自留底稿。&nbsp;</p>\n\n<p>六、奖项设置： &ldquo;奔达杯&rdquo;第六届汽车轮毂（概念）设计大赛最佳作品设</p>\n\n<p>金奖 1名 奖金 20000.00元&nbsp; 获奖证书一本，奖杯1座</p>\n\n<p>银奖 3名 奖金 8000.00元 获奖证书一本，奖杯1座</p>\n\n<p>铜奖 6名 奖金 3000.00元 获奖证书一本，奖杯1座</p>\n\n<p>优胜奖 30名 奖金 500.00元 获奖证书一本&nbsp;</p>\n\n<p>七、大赛主要活动 由评委会以公开 、公正、公平的原则组织评选，分初评和总评，评选将依据详细的《评选细则》为标准。</p>\n\n<p>初评和总评：由铝车轮质量协会（CAW）、广州美术学院工业设计学院、湖南大学设计艺术学院、燕山大学等专家评审，并委托公证处公证。 举行本次大赛的颁奖礼，表彰优秀获奖者 最佳作品、优秀作品的将由主办机构在2017年8月上海车轮博览会上展示 获奖名单在主办机构网站对外公布。&nbsp;</p>\n\n<p>八、参赛办法 参赛资格，相关设计学生及其他专业人士 参赛者必需填写参评登记表及作品效果图一同寄给大赛组委会办公室,申请表格附件下载，复印有效。 提交作品的截止日期：2017年7月15日(以邮戳为准)&nbsp;</p>\n\n<p>具体联络方式如下： 参评作品邮寄或电邮至： 江苏泗阳经济开发区轮毂产业园桂林路6号轮毂大厦A区二楼201室</p>\n\n<p>邮编：223700</p>\n\n<p>&quot;奔达杯&quot; 第六届汽车轮毂（概念）设计大赛组委会收</p>\n\n<p>联系人：樊红 联系电话：18936978580 朱捷 联系电话：18360245852</p>\n\n<p>电 话：0527-80833166</p>\n\n<p>传 真：0527-80833158</p>\n\n<p>大赛官方网站：www.chinacaw.org.cn E-mail: sylgcy@163.com&nbsp;</p>\n\n<p>九、其他说明</p>\n\n<p>1、 所有参赛作品一概不予退件，所有权属于主办机构，未经主办机构许可得奖者不得擅自公开发表其作品。</p>\n\n<p>2、 奖金为税前，获奖者按国家规定，依法缴纳个人所得税。</p>\n\n<p>3、 组委会对本次大赛拥有最终解释权。&nbsp;</p>\n\n<p>附件下载： 1. 参赛申请表 2. 版式要求图例&nbsp;</p>', '<h1>China-US friendship helps build demonstration farm</h1>\n\n<p>By ZHANG YU and YAN DONGJIE in Chengde, Hebei | China Daily | Updated: 2017-09-25 04:14 <img src=\"http://www.chinadaily.com.cn/world/tplimages/99322.files/f_art.gif\" /> <img src=\"http://www.chinadaily.com.cn/world/tplimages/99322.files/w_art.gif\" /> <img src=\"http://www.chinadaily.com.cn/world/tplimages/99322.files/in_art.gif\" /> <img src=\"http://www.chinadaily.com.cn/world/tplimages/99322.files/more_art.gif\" /></p>\n\n<p>&nbsp;</p>\n\n<table align=\"center\" style=\"height:202px; width:584px\">\n	<tbody>\n		<tr>\n			<td style=\"vertical-align:center\"><a href=\"http://www.chinadaily.com.cn/world/2017-09/25/content_32440722_2.htm\" target=\"_self\"><img alt=\"China-US friendship helps build demonstration farm\" src=\"http://www.chinadaily.com.cn/world/images/attachement/jpg/site1/20170924/180373d28c101b32567b29.jpg\" /></a></td>\n		</tr>\n		<tr>\n			<td style=\"vertical-align:center\">\n			<p>US Ambassador Terry Branstad, Hebei Party chief Zhao Kezhi, second from left, and Qu Dongyu, third from left, vice-minister of agriculture, are among officials at the launch ceremony of a demonstration farm in Luanping county, North China&#39;s Hebei province on Saturday. ZOU HONG / CHINA DAILY</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p><strong>Expertise from Iowa will be put to use in Hebei, officials explain</strong></p>\n\n<p>Agriculture is especially important to the people of China and the United States, and agricultural cooperation contributes to stronger ties between the two countries, the US ambassador to China said at a launch ceremony of a demonstration farm on Saturday.</p>\n\n<p>The China-US Friendship Demonstration Farm, in Luanping county, Hebei province, covers an area of about 1,330 hectares. It&#39;s modeled after a farm near Maxwell, Iowa, owned by Rick and Martha Kimberley and their son Grant.</p>', '5', '2017-09-25 20:47:27', '1', '1', '14', '0');
INSERT INTO `pet`.`game` (`ID`, `PIC_FILE_ID`, `DOC_FILE_ID`, `NAME_CN`, `NAME_EN`, `DESCRIPTION_CN`, `DESCRIPTION_EN`, `LAST_UPDATE_ADMIN_ID`, `LAST_UPDATE_TIME`, `PRIORITY`, `IS_ENABLE`, `VISIT_CNT`, `FAV_CNT`) VALUES ('3', '29', NULL, '中国的新一代气象卫星投入使用', 'China\'s new-generation weather satellite put into service', '<p>北京&mdash;&mdash;Fengyun-4A卫星,中国第一代地球静止轨道气象卫星,是周一投入运营,说国家科学、技术和国防工业。</p>\n\n<p>卫星平台上测试,负载和地面应用系统已经完成其在轨运行期间,奥巴马政府在一份声明中说。</p>\n\n<p>卫星帮助改善国家的天气和气候预测,在其在轨运行和测试结果见过目标,它说。</p>\n\n<p>其成功的操作使中国成为一个领导者在发展中地球静止轨道气象卫星,根据声明。</p>\n\n<p>从西昌卫星发射中心发射的卫星是在中国西南部的四川省12月11日,2016年。</p>', '<p>BEIJING -- Fengyun-4A satellite, the first of China&#39;s second-generation geostationary orbiting weather satellites, was put into operation Monday, said the State Administration of Science, Technology and Industry for National Defence.</p>\n\n<p>Tests on the satellite platform, payload and ground application system have been completed during its in-orbit operation, the administration said in a statement.</p>\n\n<p>The satellite has helped improve the country&#39;s weather and climate forecasts, and test results during its in-orbit operation have met targets, it said.</p>\n\n<p>Its successful operation enables China to become a leader in developing geostationary orbiting weather satellites, according to the statement.</p>\n\n<p>&nbsp;</p>', '5', '2017-09-25 20:38:27', '1', '1', '2', '0');
INSERT INTO `pet`.`game` (`ID`, `PIC_FILE_ID`, `DOC_FILE_ID`, `NAME_CN`, `NAME_EN`, `DESCRIPTION_CN`, `DESCRIPTION_EN`, `LAST_UPDATE_ADMIN_ID`, `LAST_UPDATE_TIME`, `PRIORITY`, `IS_ENABLE`, `VISIT_CNT`, `FAV_CNT`) VALUES ('4', '18', NULL, '更聪明的机器人', 'Smartening the world with robots', '<p><strong>中国公司专门从事机器人、自动化、人工智能系统最高统治 </strong></p>\n\n<p>场景1:Canadai&frac14;一队机器人组装各种汽车零部件通用汽车(General Motors)在一个与各种工厂汽车工厂嗡嗡作响的声音。</p>\n\n<p>场景2:Russiai&frac14;仓库的帮助下得到了超现代的边缘一个先进的自动化物流系统。</p>\n\n<p>场景3:Malaysiai&frac14;服务机器人&ldquo;雇佣&rdquo;房地产公司欢迎潜在购房者一个受欢迎的公寓和蜡雄辩的平坦的独特功能。</p>\n\n<p>的机器人,自动化和人工智能或AI,领先的产品和系统,像上面所描述的,中国制造的,但世界服务。</p>\n\n<p>日经亚洲报道评论说大约90%的个人机器人的IFA消费电子展会,展出在柏林举行本月早些时候,被中国企业开发和生产。</p>\n\n<p>Shenyang-based Siasun机器人与自动化有限公司是最大的,其市场价值335亿元(51亿美元)。 其产品目前出口到30多个国家和地区,其中一半参与带和道路计划。</p>', '<p><strong>Chinese firms specializing in robotics, automation, AI systems reign supreme</strong></p>\n\n<p>Scene 1: Canada－A team of robots assembles various car parts for General Motors at an automobile plant humming with assorted factory sounds.</p>\n\n<p>Scene 2: Russia－A warehouse gets the ultra-modern edge with the help of an advanced automated logistics system.</p>\n\n<p>Scene 3: Malaysia－A service robot &quot;employed&quot; by a real estate company welcomes prospective homebuyers to a popular apartment and waxes eloquent on the flat&#39;s unique features.</p>\n\n<p>That&#39;s the power of robotics, automation and artificial intelligence or AI, where leading products and systems, like the ones described above, are made in China but serve the world.</p>\n\n<p>A report in Nikkei Asian Review said about 90 percent of the personal robots on display at the IFA consumer electronics trade show, which was held in Berlin earlier this month, were developed and manufactured by Chinese companies.</p>\n\n<p>Shenyang-based Siasun Robot &amp; Automation Co is the largest among them, in terms of its market value of 33.5 billion yuan ($5.1 billion). Its products are now exported to more than 30 countries and regions, half of which are participating in the Belt and Road Initiative.</p>', '5', '2017-09-25 20:49:57', '1', '1', '2', '0');
