mapper.select:
select * from prod_type_biz where 1=1


		and NAME_CN like CONCAT('%',#{condition.nameCn,
		jdbcType=VARCHAR},'%')


		and NAME_EN like CONCAT('%',#{condition.nameEn,
		jdbcType=VARCHAR},'%')


		and IS_ENABLE = #{condition.isEnable}



	order by ${orderBySql}

limit ${start},${length}
